from django.urls import path, re_pathfrom . import viewsapp_name = 'shop'urlpatterns = [    path('', views.category, name='index'),    path('category/<int:pk>/', views.category_detail, name='category_detail'),    path('shop/<int:pk>/', views.shop_detail, name='shop_detail'),    path('shop/<int:pk>/review/new/', views.ReviewCreateView.as_view(), name='review_new'),    path('<int:shop_pk>/order/new/', views.order_new, name='order_new'),    re_path(r'^(?P<shop_pk>\d+)/order/(?P<merchant_uid>[\da-f\-]{36})/pay/$', views.order_pay, name='order_pay'),    path('profile/', views.profile, name='profile'),]